mapper.select:
-- 测试 not exists子查询
  SELECT * FROM table_a AS a
  WHERE
  NOT EXISTS (
  SELECT * FROM table_a WHERE b_id = a.b_id AND create_time>a.create_time
  )
  AND b_id=#{bId}
  AND age IN (13,22,18)
  GROUP BY id;

update.foreach:
UPDATE table_b set
a_name = #{item.aName}
WHERE  id = #{item.id}
